import { Fragment, useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";

import { useSelector } from "react-redux";

function App() {
  const cartSelector = useSelector((state) => state.page.isDisplayShoppingCart);
  const cart = useSelector((state) => state.product.item);
  const notification = useSelector(state => state.page.notification);

  useEffect(() => {
    const sendData = async () => {
      const response = await fetch(
        "https://react-http-efc44-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
        { method: "put", body: JSON.stringify(cart) }
      );
    };
    sendData();
  }, [cart]);

  return (
    <Fragment>
      {notification && <Notification />}
      <Layout>
        {cartSelector && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
