import { createSlice } from "@reduxjs/toolkit";
import { pageAction } from "./pageSlice";

const defaultProduct = {
  item: [],
  totalQuantity: 0,
};
const productSlice = createSlice({
  name: "product",
  initialState: defaultProduct,
  reducers: {
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.item.find((item) => item.id === newItem.id);

      if (!existingItem) {
        state.item.push({
          id: newItem.id,
          name: newItem.name,
          price: newItem.price,
          quantity: 1,
          totolPrice: newItem.price,
        });
      } else {
        existingItem.quantity++;
        existingItem.totolPrice = existingItem.totolPrice + newItem.price;
      }

      //UpdateItem in Cart
      state.totalQuantity++;
    },

    removeItemFromCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.item.find((item) => item.id === newItem.id);

      if (existingItem.quantity === 1) {
        const filteredItem = state.item.filter(
          (item) => item.id !== newItem.id
        );
        state.item = filteredItem;
      } else {
        existingItem.quantity--;
        existingItem.totolPrice = existingItem.totolPrice - existingItem.price;
      }

      //UpdateItem in Cart
      state.totalQuantity--;
    },
  },
});

export const sendDate = () => {
  return (dispatch) => {
    //Start Sending Data
    dispatch(
      pageAction.setNotification({
        status: "Sending",
        title: "Sending...",
        message: "Data is sending...",
      })
    );
  }

  const sendRequest = async () => {
    const response = await fetch(
      "https://react-http-efc44-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
      { method: "put", body: JSON.stringify(cart) }
    );

    if (!response.ok) {
      throw new Error("Sending Cart Data Failed");
    }
  }

}


export const productAction = productSlice.actions;
export default productSlice.reducer;
