import { useSelector, useDispatch } from "react-redux";
import { Fragment, useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { pageAction } from "./store/pageSlice";

let initDisplay = true;

function App() {
  const cartSelector = useSelector((state) => state.page.isDisplayShoppingCart);
  const cart = useSelector((state) => state.product.item);
  const notification = useSelector((state) => state.page.notification);
  const dispatch = useDispatch();

  useEffect(() => {
    const sendData = async () => {
      //Start Sending Data
      dispatch(
        pageAction.setNotification({
          status: "Sending",
          title: "Sending...",
          message: "Data is sending...",
        })
      );

      const response = await fetch(
        "https://react-http-efc44-default-rtdb.asia-southeast1.firebasedatabase.app/",
        { method: "put", body: JSON.stringify(cart) }
      );

      if(!response.ok) {
        throw new Error('Sending Cart Data Failed')
      }

      //Sending Data Complete
      dispatch(
        pageAction.setNotification({
          status: "success",
          title: "success",
          message: "Data is send successfully",
        })
      );

      //Reset Notification
      setTimeout(() => {
        dispatch(pageAction.resetNotification());
      }, 1000);
    };

    if(initDisplay === true)
    {
      initDisplay = false;

      sendData().catch((error) => {
        dispatch(
          pageAction.setNotification({
            status: "error",
            title: "Error",
            message: "Sending Data Failed",
          })
        )
      });
    }
  }, [cart, dispatch]);

  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {cartSelector && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
