import { createSlice } from "@reduxjs/toolkit";

const defaultProduct = {
    cartItems: {}
}
const productSlice = createSlice({
    name: 'product',
    initialState: defaultProduct,
    reducers: {
        addToCart() {}
    }
})

export const productAction = productSlice.actions
export default productSlice.reducer
